import { useState } from 'react'

const Button = (props) => {
  return (
    <button onClick={props.handleClick}>
      {props.text}
    </button>
  )
}

const Statistics = ({number1, number2, number3}) => {
  return (
    <div>
      <p>good {number1}</p>
      <p>bad {number2}</p>
      <p>neutral {number3}</p>
      <p>average {number1+number2+number3}</p>
      <p>positive {(number1+number2)/(number1+number2+number3)*100}%</p>
    </div>
  )
}

const History = (props) => {
  if (props.allClicks.length === 0) {
    return (
    <div>
      the app is used by pressing the buttons
    </div>
    )
  }
}

const App = () => {
  const [allClicks, setAll] = useState([])
  // tallenna napit omaan tilaansa
  const [good, setGood] = useState(0)
  const [neutral, setNeutral] = useState(0)
  const [bad, setBad] = useState(0)
  const increasegood = () => {
    setGood(good + 1)
    setAll(allClicks.concat('G'))
  }
  const increaseneutral = () => {
    setNeutral(neutral + 1)
    setAll(allClicks.concat('N'))
  }
  const increasebad = () => {
    setAll(allClicks.concat('B'))
    setBad(bad + 1)
  }

  return (
    <div>
      <h1>Give feedback</h1>
      <Button
        handleClick={increasegood}
        text='good'
        />
      <Button
        handleClick={increaseneutral}
        text='neutral'
        />
      <Button 
        handleClick={increasebad}
        text='bad'
        />
      <h1>Statistics</h1>
      <Statistics number1={good} number2={neutral} number3={bad}/>
    </div>
  )
}

export default App
