import { useState } from 'react'

const Button = (props) => {
  return (
    <button onClick={props.handleClick}>
      {props.text}
    </button>
  )
}

const Design = ({number1, number2, number3}) => {
  return (
    <div>
      <p>good {number1}</p>
      <p>bad {number2}</p>
      <p>neutral {number3}</p>
      <p>average {number1+number2+number3}</p>
      <p>positive {(number1+number2)/(number1+number2+number3)}%</p>
    </div>
  )
}

const App = () => {
  // tallenna napit omaan tilaansa
  const [good, setGood] = useState(0)
  const [neutral, setNeutral] = useState(0)
  const [bad, setBad] = useState(0)
  const increasegood = () => setGood(good + 1)
  const increaseneutral = () => setNeutral(neutral + 1)
  const increasebad = () => setBad(bad + 1)

  return (
    <div>
      <h1>Give feedback</h1>
      <Button
        handleClick={increasegood}
        text='good'
        />
      <Button
        handleClick={increaseneutral}
        text='neutral'
        />
      <Button 
        handleClick={increasebad}
        text='bad'
        />
      <h1>Statistics</h1>
      <Design number1={good} number2={neutral} number3={bad}/>
    </div>
  )
}

export default App
